  return (
    <React.Fragment>
      <Typography component="h3" variant="h5" style={{ color: "#c16b53", fontWeight: "bold", paddingLeft: "7px" }}>
        Your tickets
      </Typography>
      <Table size="small" style={{maxWidth: "100%"}}>
        <TableHead>
          <TableRow>
            <TableCell><b>Purchase date</b></TableCell>
            <TableCell><b>Visit date</b></TableCell>
            <TableCell><b>Ticket type</b></TableCell>
            <TableCell><b>Ticket price</b></TableCell>
            <TableCell align="right" style={{paddingRight: "5%"}}><b>Use</b></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {tickets.map((ticket) => (
            <TableRow>
              <TableCell>{ticket.purchaseDate}</TableCell>
              <TableCell>{ticket.visitDate}</TableCell>
              <TableCell>{ticket.ticketType}</TableCell>
              <TableCell>{ticket.ticketPrice}</TableCell>
              <TableCell>{ticket.isUsable}</TableCell>
              <TableCell align="right" style={{paddingRight: "3%"}}><Button /></TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

    </React.Fragment>
  );
