import React, {useState} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';

const TextFieldEmail = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleClick = () => {
      <Link href={"login?email=" + this.state.email + "&password=" + this.state.password}></Link>
    };
  
    return (
      <form>
        <TextField
          variant="outlined"
          margin="normal"
          fullWidth
          name="email"
          label="Email"
          type="email"
          autoComplete="current-email"
          autoFocus
          value={email}
          onChange={(event) => {setEmail(event.target.value)}} //whenever the text field change, you save the value in state
         />
        <TextField
          variant="outlined"
          margin="normal"
          fullWidth
          name="password"
          label="Password"
          type="password"
          id="password"
          autoComplete="current-password"
          autoFocus
          value={password}
          onChange={(event) => {setPassword(event.target.value)}} //whenever the text field change, you save the value in state
         />
        <FormControlLabel
          control={<Checkbox value="remember" color="primary" />}
          label="Remember me"
        />
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
        //  className={classes.submit}
          onClick={handleClick}
        >
          Sign In
        </Button>
    </form>
    );
  };

  export default TextFieldEmail;
