import React, {useState} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import AccountBalanceIcon from '@material-ui/icons/AccountBalance';
import IconButton from '@material-ui/core/IconButton';
import { Link } from "react-router-dom";

export default function Tickets(props) {
  const [data, upDateData] = React.useState([]);
  const [firstLoad, setLoad] = React.useState(true);
  let isLoading = true;


  const visitorTicketsURL = "/login/tickets?visitorId=" + props.visitor

  async function sampleFunc() {
    let response = await fetch(visitorTicketsURL);
    let body = await response.json();
    upDateData(body);
  }

  if (firstLoad) {
    sampleFunc();
    setLoad(false);
  }

  if (data.length > 0) isLoading = false;

  console.log(props.visitor)

  return (
    <React.Fragment>
      <Typography component="h3" variant="h5" style={{ color: "#c16b53", fontWeight: "bold", paddingLeft: "7px" }}>
        Your tickets
      </Typography>
      <Table size="small" style={{maxWidth: "100%"}}>
        <TableHead>
          <TableRow>
            <TableCell><b>Purchase date</b></TableCell>
            <TableCell><b>Visit date</b></TableCell>
            <TableCell><b>Ticket type</b></TableCell>
            <TableCell><b>Ticket price</b></TableCell>
            <TableCell align="right" style={{paddingLeft: "0px"}}><b>Use</b></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
        {data.map((row) => (
          <TableRow key={row.ticketId}>
            <TableCell>{row.purchaseDate}</TableCell>
            <TableCell>{row.visitDate}</TableCell>
            <TableCell>{row.ticketType}</TableCell>
            <TableCell>{row.ticketPrice}</TableCell>
          </TableRow>
        ))}
        </TableBody>
      </Table>

    </React.Fragment>
  );
}
