import React from 'react';
import { Link } from "react-router-dom";
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import AreaHubBG from "../../img/areahub.png";

const useStyles = makeStyles((theme) => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(8, 0, 6),
    backgroundImage: `url(${AreaHubBG})`,
    backgroundRepeat: "no-repeat",
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    minHeight: '300px',
		minWidth: '300px',
    display: 'flex',
    flexDirection: 'column',
		marginLeft: "30px",
		marginTop: "30px",

  },
  footer: {
    backgroundColor: theme.palette.background.paper,
  },
}));

export default function Artwork() {
  // eslint-disable-next-line
  const classes = useStyles();

  const [data, upDateData] = React.useState([]);
  const [firstLoad, setLoad] = React.useState(true);

  // eslint-disable-next-line
  let isLoading = true;
  let artworkId = window.location.pathname.replace("Baroque", "").replace("Cubism", "").replace("Impressionism", "").replace("Renaissance", "").replace("/areaHub/", "").replace("/artwork/", "");
	console.log(artworkId)

  async function sampleFunc() {
    let response = await fetch("/api/artwork/" + artworkId);
    let body = await response.json();
    upDateData(body);
  }

  if (firstLoad) {
    sampleFunc();
    setLoad(false);
  }

  console.log(data[0])

  if (data.length > 0) isLoading = false;

  return (
    <div style={{backgroundColor: "#dac0b8", minHeight:"100vh"}}>
      {/* Hero unit */}
      <div className={classes.heroContent} style={{backgroundColor: "#d69e8d"}}>
        <Container maxWidth="sm">
          <Typography component="h3" variant="h3" align="center" color="textPrimary" gutterBottom style={{color: "#975643"}}>
              {data.name}
          </Typography>
          <Typography variant="h4" align="center" color="textSecondary" paragraph style={{color: "#975643"}}>
							{data.author}
          </Typography>
        </Container>
      </div>
      <Container className={classes.cardGrid} maxWidth="md">
        <Grid container spacing={4}>
		


							<Grid item xs={12} sm={6} md={12} align-items-sm-center justify-sm-flex-end
>
						<CardMedia
								className={classes.cardMedia}
								component={() => (<img border="5px solid #555 "src={"data:image/png;base64," + data.image} />)}
						/>
                <Card className={classes.card} style={{height: "-webkit-fill-available", width: "-webkit-fill-available"}}>

                  <CardContent className={classes.cardContent}>
                    <Typography gutterBottom variant="h5" component="h2">
                      Made: {data.date}
											{data.completeDescription}
                    </Typography>
                    <Typography>
										{/*testo*/}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>

        </Grid>
      </Container>  
    </div>
  );
}
