import React, {useState} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';

// Generate Order Data
function createData(id, date, name, shipTo, paymentMethod, amount) {
  return { id, date, name, shipTo, paymentMethod, amount };
}

const rows = [
  createData(2310, "2021-02-01", "2020-12-27", "Adult", String(8.99), /*button to use the ticket */),
];



export default function Orders() {

  const [visitorId, setVisitorId] = useState("");
  const [ticketsNumber, setTicketsNumber] = useState("");
  const [purchaseDate, setPurchaseDate] = useState("");
  const [visitDate, setVisitDate] = useState("");
  const [ticketType, setTicketType] = useState("");
  const [ticketPrice, setTicketPrice] = useState("");
  const [isUsable, setIsUsable] = useState("");

  let tickets = [
    {"purchaseDate": "",
     "visitDate": "",
     "ticketType": "",
     "ticketPrice": "",
     "isUsable" : ""
    }
  ]

  let email = (window.location.pathname.replace("/dashboard/", ""));

  let visitorIdURL = fetch("/login/userInfo/visitorId?email=" + email);

  visitorIdURL.then(visitorIdURL => {
    return visitorIdURL.json();
  }).then(visitorIdP => {
    const visitorId = visitorIdP[0]
    setVisitorId(visitorId)
  })

  let ticketsCountURL = fetch("/login/ticketInfo/ticketNumber?visitorId=93");

  ticketsCountURL.then(ticketsCountURL => {
    return ticketsCountURL.json();
  }).then(tNumber => {
    const ticketsNumber = tNumber[0]
    setTicketsNumber(ticketsNumber)
    //console.log(ticketsNumber)
  })

    let purchaseDateURL = fetch("/login/ticketInfo/purchaseDate?visitorId=93");

    purchaseDateURL.then(purchaseDateURL => {
      return purchaseDateURL.json();
    }).then(pDate => {
      const purchaseDate = pDate[0]
      setPurchaseDate(purchaseDate)
    })


    let visitDateURL = fetch("/login/ticketInfo/visitDate?visitorId=93");

    visitDateURL.then(visitDateURL => {
      return visitDateURL.json();
    }).then(vDate => {
      const visitDate = vDate[0]
      setVisitDate(visitDate)
    })


    let ticketTypeURL = fetch("/login/ticketInfo/ticketType?visitorId=93");

    ticketTypeURL.then(ticketTypeURL => {
      return ticketTypeURL.json();
    }).then(tType => {
      const ticketType = tType[0]
      setTicketType(ticketType)
    })

    let ticketPriceURL = fetch("/login/ticketInfo/ticketPrice?visitorId=93");

    ticketPriceURL.then(ticketPriceURL => {
      return ticketPriceURL.json();
    }).then(tPrice => {
      const ticketPrice = tPrice[0]
      setTicketPrice(ticketPrice)
    })

    let usabilityURL = fetch("/login/ticketInfo/isUsable?visitorId=93");

    usabilityURL.then(usabilityURL => {
      return usabilityURL.json();
    }).then(tUsable => {
      const isUsable = tUsable[0]
      setIsUsable(isUsable)
    })

  return (
    <React.Fragment>
      <Typography component="h3" variant="h5" style={{ color: "#c16b53", fontWeight: "bold", paddingLeft: "7px" }}>
        Your tickets
      </Typography>
      <Table size="small" style={{maxWidth: "100%"}}>
        <TableHead>
          <TableRow>
            <TableCell><b>Purchase date</b></TableCell>
            <TableCell><b>Visit date</b></TableCell>
            <TableCell><b>Ticket type</b></TableCell>
            <TableCell><b>Ticket price</b></TableCell>
            <TableCell align="right" style={{paddingRight: "5%"}}><b>Use</b></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {tickets.map((ticket) => (
            <TableRow key={visitorId}>
              <TableCell>{ticket.purchaseDate}</TableCell>
              <TableCell>{ticket.visitDate}</TableCell>
              <TableCell>{ticket.ticketType}</TableCell>
              <TableCell>{ticket.ticketPrice}</TableCell>
              <TableCell>{ticket.isUsable}</TableCell>
              <TableCell align="right" style={{paddingRight: "3%"}}><Button /></TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

    </React.Fragment>
  );
}
