import React, {useState} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const LoginForm = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleClick = () => {
      console.log(email + " " + password);
    };
  
    return (
      <div>
        <TextField
          variant="outlined"
          margin="normal"
          required
          fullWidth
          autoFocus
          value={email}
          onChange={(event) => {setEmail(event.target.value)}} //whenever the text field change, you save the value in state
          />
        <TextField
          variant="outlined"
          margin="normal"
          required
          fullWidth
          type="password"
          autoComplete="current-password"
          autoFocus
          value={password}
          onChange={(event) => {setPassword(event.target.value)}} //whenever the text field change, you save the value in state
          />
        <FormControlLabel
          control={<Checkbox value="remember" color="primary" />}
          label="Remember me"
        />
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          onClick={handleClick}
        >
          Sign In
        </Button>
      </div>
    );
  };

  export default LoginForm;
