import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Avatar from "@material-ui/core/Avatar";
import GroupIcon from "@material-ui/icons/Group";
import { Link } from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import CircularProgress from "@material-ui/core/CircularProgress";
import AccountBalanceIcon from '@material-ui/icons/AccountBalance';
import IconButton from '@material-ui/core/IconButton';

const useStyles = makeStyles((theme) => ({
  table: {
    minWidth: 600,
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  paper: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    margin: `10px`,
    height: "100%",
    width: "99%",
    marginTop: theme.spacing(7),
  },
  link: {
    color: "rgba(0,0,0,0.65)",
    textDecoration: "none",
    marginLeft: "10%",
    alignSelf: "flex-start",
    "&:hover": {
      color: "rgba(0,0,0,1)",
    },
  },
}));

export default function Orders() {
  const classes = useStyles();

  const [data, upDateData] = React.useState([]);
  const [firstLoad, setLoad] = React.useState(true);
  const [visitorId, setVisitorId] = React.useState("");

  let isLoading = true;

  let email = (window.location.pathname.replace("/dashboard/", ""));
  let visitorIdURL = "/login/userInfo/visitorId?email=" + email;

  let ticketsCountURL = fetch("/login/ticketInfo/ticketNumber?visitorId=" + visitorId);

  ticketsCountURL.then(ticketsCountURL => {
    return ticketsCountURL.json();
  }).then(tNumber => {
    const ticketsNumber = tNumber[0]
    setTicketsNumber(ticketsNumber)
    //console.log(ticketsNumber)
  })


  async function sampleFunc() {
    let response = await fetch("/api/ticket/id=" + visitorId);
    let body = await response.json();
    upDateData(body);
  }

  if (firstLoad) {
    sampleFunc();
    setLoad(false);
  }

  if (data.length > 0) isLoading = false;

  return (
    <div className={classes.paper}>
      <Avatar className={classes.avatar}>
        <GroupIcon />
      </Avatar>
      <Typography component="h1" variant="h5">
        Employee Directory
      </Typography>

      {isLoading ? (
        <CircularProgress />
      ) : (
        <TableContainer
          style={{ width: "80%", margin: "0 10px" }}
          component={Paper}
        >
          <Table className={classes.table} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell><b>Purchase date</b></TableCell>
                <TableCell><b>Visit date</b></TableCell>
                <TableCell><b>Ticket type</b></TableCell>
                <TableCell><b>Ticket price</b></TableCell>
                <TableCell align="right" style={{paddingLeft: "0px"}}><b>Use</b></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
            {data.map((ticket) => (
            <TableRow>
              <TableCell align="center">{ticket.purchaseDate}</TableCell>
              <TableCell align="center">{ticket.visitDate}</TableCell>
              <TableCell align="center">{ticket.ticketType}</TableCell>
              <TableCell align="center">{ticket.ticketPrice}</TableCell>
              <TableCell align="center">{ticket.isUsable}</TableCell>
              <TableCell align="left">
                <IconButton
                    disabled = {!ticket.isUsable}
                    onClick = {console.log("reminder")} // reminder
                    component={Link}
                    to="/areaHub">
                  <AccountBalanceIcon />
                </IconButton>
              </TableCell>
            </TableRow>
          ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
      <Link className={classes.link} to="/">
        {" "}
        <Typography align="left">
          &#x2190; Head back to save data
        </Typography>{" "}
      </Link>
    </div>
  );
}
