import React, {useState} from 'react';
import Typography from '@material-ui/core/Typography';

export default function UserInfo(props) {
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [email, setEmail] = useState("");
  const [visitorId, setVisitorId] = useState("");

  let nameURL = fetch("/login/userInfo/name?email=Alelame@gmail.com", {method: "GET"});

  nameURL.then(nameURL => {
    return nameURL.json();
  }).then(visitorName => {
    const name = visitorName[0]
    setName(name)
  })

  let surnameURL = fetch("/login/userInfo/surname?email=Alelame@gmail.com", {method: "GET"});

  surnameURL.then(surnameURL => {
    return surnameURL.json();
  }).then(visitorSurname => {
    const surname = visitorSurname[0]
    setSurname(surname)
  })

  let visitorIdURL = fetch("/login/userInfo/visitorId?email=Alelame@gmail.com", {method: "GET"});

  visitorIdURL.then(visitorIdURL => {
    return visitorIdURL.json();
  }).then(visitorEmail => {
    const visitorId = visitorId[0]
    setVisitorId(visitorId)
  })


  return (
    <div>
      <Typography component="h1" variant="h3" style={{color: "#c16b53", fontWeight: "bold", paddingLeft: "7px" }}>
        Welcome, 
      </Typography>
      <Typography component="p" variant="h4" style={{color: "#c2968a", paddingLeft: "7px"}} /* name and surname area */>
        {name} {surname}
      </Typography>
      <Typography color="textSecondary" style={{paddingLeft: "7px"}} /* email area */ >
        {email}, {visitorId.toString()}
      </Typography>
    </div>




  );
}
