import React from 'react';
import { Link } from "react-router-dom";
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import AreaHubBG from "../../img/areahub.png";

const useStyles = makeStyles((theme) => ({
  
}));

export default function Artwork() {
  // eslint-disable-next-line
  const classes = useStyles();

  const [data, upDateData] = React.useState([]);
  const [firstLoad, setLoad] = React.useState(true);

  // eslint-disable-next-line
  let isLoading = true;
  let artworkId = window.location.pathname.replace("Baroque", "").replace("Cubism", "").replace("Impressionism", "").replace("Renaissance", "").replace("/areaHub/", "").replace("/artwork/", "");
	console.log(artworkId)

  async function sampleFunc() {
    let response = await fetch("/api/artwork/" + artworkId);
    let body = await response.json();
    upDateData(body);
  }

  if (firstLoad) {
    sampleFunc();
    setLoad(false);
  }

  console.log(data[0])

  if (data.length > 0) isLoading = false;

  return (
    <div style={{backgroundColor: "#dac0b8", minHeight:"100vh"}}>
      {/* Hero unit */}
      <div className={classes.heroContent} style={{backgroundColor: "#d69e8d"}}>
        <Container maxWidth="sm">
          <Typography component="h1" variant="h2" align="center" color="textPrimary" gutterBottom style={{color: "#975643"}}>
            Welcome
          </Typography>
          <Typography variant="h5" align="center" color="textSecondary" paragraph style={{color: "#975643"}}>
              Have a nice stay and enjoy your ride in this new Museum Experience.
          </Typography>
        </Container>
      </div>
      <Container className={classes.cardGrid} maxWidth="md">
        <Grid container spacing={4}>
         
         
              <Card>
							<CardMedia
                  className={classes.cardMedia}
                  component={() => (
                    <img                      
                      src={"data:image/png;base64," + data.image}
                    />
                  )}
                />
              </Card>
          
							<CardContent className={classes.cardContent}>
                <Typography gutterBottom variant="h5" component="h2">
                    
                  </Typography>      
                  <Typography gutterBottom variant="h8" component="h8">
                    <Card className={classes.card}>dsfekmdgm</Card>
                  </Typography>
                </CardContent>
        </Grid>
      </Container>  
    </div>
  );
}
